import React, { useState, useEffect, useCallback } from 'react';
import sendRequest from '../functions/sendRequest';
import useAsync from '../hooks/useAsync';

export default function PostsList() {

    //const [posts, setPosts] = useState([])

    const loadPosts = useCallback(
        async () => await sendRequest(
            'GET',
            "https://testone-8ac4d-default-rtdb.europe-west1.firebasedatabase.app/posts.json"
        ), [sendRequest])

    const { execute, status, value, error } = useAsync(loadPosts, true);

    useEffect(() => { execute }, [value]);

    // useEffect(() => {
    //     loadPosts().then(value => console.log(value))
    //         .then(value => setPosts(value))
    //         .then(console.log(posts.length))

    // }, []);


    return (
        <>
            {status === "success" && <div>готов</div>}
            {status === "success" && value.map(post => <div key={post.id}>{post.content}</div>)}
        </>

    )
}
