import React, { useEffect, useCallback } from 'react';
import { useSelector } from 'react-redux';
import sendRequest from '../functions/sendRequest';
import useAsync from '../hooks/useAsync';
import Post from './Post';

export default function PostsList() {

    const state = useSelector((state) => state.master);

    const loadPosts = useCallback(
        async () => await sendRequest(
            'GET',
            "https://testone-8ac4d-default-rtdb.europe-west1.firebasedatabase.app/posts.json"
        ), [sendRequest]);

    const deletePost = (id) => {
        sendRequest(
            'DELETE',
            `https://testone-8ac4d-default-rtdb.europe-west1.firebasedatabase.app/posts/${id}.json`
        )

    };

    const { execute, status, value, error } = useAsync(loadPosts, false);

    useEffect(() => { execute() }, [state.text]);

    useEffect(() => { execute() }, []);

    return (
        <>
            {status === "success" && <div>готов</div>}
            {status === "success" && value.map(post =>
                <Post key={post.id} content={post.content} deletePost={deletePost} />)}
        </>

    )
}
