import React, { useState, useEffect, useCallback } from 'react';
import sendRequest from '../functions/sendRequest';

export default function PostsList() {

    const [posts, setPosts] = useState([]);

    const loadPosts = useCallback(
        async () => await sendRequest(
            'GET',
            "https://testone-8ac4d-default-rtdb.europe-west1.firebasedatabase.app/posts.json"
        ), [sendRequest])

    useEffect(() => {
        loadPosts().then(value => console.log(value))
            .then(value => setPosts(value))
            .then(console.log(posts.length))
        //.then((value) => { let data = value; console.log(data) })
        //
        //.then(console.log(posts))
        //
        //const list = Object.keys(data).map(key => ({ ...data[key], id: key }))
        //console.log(posts)
    }, []);


    return (
        <>
            {/* {posts.length === 80 && posts.map(post => <div>{post.content}</div>)} */}
            {posts && posts.map(post => <div>{post.content}</div>)}
        </>

    )
}
